import React, { Component } from 'react';
import './Slideshow.css'; // Import your CSS file

class Slideshow extends Component {
  constructor() {
    super();
    this.state = {
      currentSlide: 0,
      slides: [
        {
          imageUrl: 'image1.jpg',
          title: 'Slide 1',
          text: 'This is the first slide.',
          link: 'https://example.com',
        },
        {
          imageUrl: 'image2.jpg',
          title: 'Slide 2',
          text: 'This is the second slide.',
          link: 'https://example.com',
        },
        // Add more slides as needed
      ],
    };
  }

  componentDidMount() {
    this.startSlideshow();
  }

  componentWillUnmount() {
    this.stopSlideshow();
  }

  startSlideshow = () => {
    this.slideshowInterval = setInterval(() => {
      this.nextSlide();
    }, 5000); // Change slide every 5 seconds (adjust as needed)
  }

  stopSlideshow = () => {
    clearInterval(this.slideshowInterval);
  }

  nextSlide = () => {
    const { currentSlide, slides } = this.state;
    this.setState({ currentSlide: (currentSlide + 1) % slides.length });
  }

  render() {
    const { currentSlide, slides } = this.state;

    return (
      <>
      <div class="slideshow-container">
    <div class="slide">
      <img src="image1.jpg" alt="Image 1"/>
      <div class="slide-text">
        <h2>Slide 1</h2>
        <p>This is the first slide.</p>
        <a href="https://example.com">Learn More</a>
      </div>
    </div>
    <div class="slide">
      <img src="image2.jpg" alt="Image 2"/>
      <div class="slide-text">
        <h2>Slide 2</h2>
        <p>This is the second slide.</p>
        <a href="https://example.com">Learn More</a>
      </div>
    </div>

  </div>
  <button id="prevBtn" class="prev" onclick="plusSlides(-1)">&#8249;</button>
  <button id="nextBtn" class="next" onclick="plusSlides(1)">&#8250;</button>

      <div className="slideshow-container">
        {slides.map((slide, index) => (
          <div key={index} className={`slide ${index === currentSlide ? 'active' : ''}`}>
            <img src={slide.imageUrl} alt={slide.title} />
            <div className="slide-text">
              <h2>{slide.title}</h2>
              <p>{slide.text}</p>
              <a href={slide.link} target="_blank" rel="noopener noreferrer">Learn More</a>
            </div>
          </div>
        ))}
      </div>
      </>
    );
  }
}

export default Slideshow;
